import {Controller} from "@hotwired/stimulus";
import '@kanety/stimulus-static-actions';

export default class UIAlertDialog extends Controller {
    static targets = ['trigger', 'content', 'action', 'cancel', 'overlay'];
    static actions = [
        ['trigger', 'click->open'],
        ['cancel', 'click->close'],
        ['action', 'click->close']
    ];

    setDataState(target, state) {
        target.setAttribute('data-state', state);
    }

    setAriaAttributes(target, expanded) {
        target.setAttribute('aria-expanded', expanded);
        target.setAttribute('aria-hidden', expanded === 'true' ? 'false' : 'true');
        target.setAttribute('data-aria-hidden', expanded === 'true' ? 'false' : 'true');
    }

    setVisualAttributes(target, hidden, display) {
        target.hidden = hidden;
        if (display !== null) {
            target.style.display = display;
        }
    }

    open(e) {
        this.triggerButton = e.target;
        this.setDataState(this.element, 'open');
        this.setDataState(this.triggerButton, 'open');
        this.setDataState(this.contentTarget, 'open');
        this.setDataState(this.overlayTarget, 'open');
        this.setAriaAttributes(this.triggerButton, 'true');
        this.setVisualAttributes(this.contentTarget, false, null);
        this.setVisualAttributes(this.overlayTarget, false, null);
        this.contentTarget.style.removeProperty('display');
    }

    close() {
        this.setDataState(this.element, 'closed');
        this.setDataState(this.triggerButton, 'closed');
        this.setDataState(this.contentTarget, 'closed');
        this.setDataState(this.overlayTarget, 'closed');
        this.setAriaAttributes(this.triggerButton, 'false');

        setTimeout(() => {
            this.setVisualAttributes(this.contentTarget, true, 'none');
            this.setVisualAttributes(this.overlayTarget, true, null);
        }, 120)
    }
}

