import {Controller} from "@hotwired/stimulus";
import EmblaCarousel from 'embla-carousel'
import '@kanety/stimulus-static-actions';

export default class UICarousel extends Controller {
    static targets = ['carousel', 'content', 'item', 'previous', 'next']
    static values = {
        orientation: String,
        options: Object
    }
    static actions = [
        ['previous', 'click->scrollPrev'],
        ['next', 'click->scrollNext'],
    ];

    initialize() {
        this.api = EmblaCarousel(this.carouselTarget, this.optionsValue)

        this.updateScrollButtonsState();
        this.updateClasses();
    }

    scrollPrev() {
        this.api.scrollPrev();
        this.updateScrollButtonsState();
    }

    scrollNext() {
        this.api.scrollNext();
        this.updateScrollButtonsState();
    }

    updateScrollButtonsState() {
        this.previousTarget.disabled = !this.api.canScrollPrev();
        this.nextTarget.disabled = !this.api.canScrollNext();
    }

    updateClasses() {
        const isHorizontal = this.orientationValue === 'horizontal';

        const contentClass = isHorizontal ? ['-ml-4'] : ['-mt-4', 'flex-col'];
        this.addClassesToElement(this.contentTarget, contentClass);

        const itemClass = isHorizontal ? ['pl-4'] : ['pt-4'];
        this.itemTargets.forEach(itemTarget => this.addClassesToElement(itemTarget, itemClass));

        const previousClass = isHorizontal ? ['-left-12', 'top-1/2', '-translate-y-1/2'] : ['-top-12', 'left-1/2', '-translate-x-1/2', 'rotate-90'];
        this.addClassesToElement(this.previousTarget, previousClass);

        const nextClass = isHorizontal ? ['-right-12', 'top-1/2', '-translate-y-1/2'] : ['-bottom-12', 'left-1/2', '-translate-x-1/2', 'rotate-90'];
        this.addClassesToElement(this.nextTarget, nextClass);
    }

    addClassesToElement(element, classes) {
        classes.forEach(className => element.classList.add(className));
    }
}

