import {Controller} from "@hotwired/stimulus";

const ACCORDION_KEYS = ['Home', 'End', 'ArrowDown', 'ArrowUp', 'ArrowLeft', 'ArrowRight'];

export default class UIAccordion extends Controller {
    static targets = ["item", "trigger", "content"];
    static values = {type: String}

    initialize() {
        // validate value type
        if (!['single', 'multiple'].includes(this.typeValue)) {
            throw new Error('Failed prop type: Invalid prop `type` supplied to `accordion:root`. Expected one of `single | multiple`.')
        }

        this.setIndicesAndStyles(this.itemTargets);
        this.setIndicesAndStyles(this.triggerTargets);
        this.setIndicesAndStyles(this.contentTargets, content => {
            content.style.setProperty('--radix-accordion-content-height', 'var(--radix-collapsible-content-height)');
            content.style.setProperty('--radix-accordion-content-width', 'var(--radix-collapsible-content-width)');
        });
    }

    setIndicesAndStyles(targetsArray, extraOperation = () => {
    }) {
        targetsArray.forEach((item, index) => {
            item.setAttribute("data-index", index);
            extraOperation(item);
        });
    }

    keydown(event) {
        if (!ACCORDION_KEYS.includes(event.key)) return;

        const target = event.target;
        const orientation = this.element.getAttribute("data-orientation");
        const isDirectionLTR = this.element.getAttribute("data-direction") === 'ltr';
        const triggerIndex = parseInt(target.getAttribute("data-index"));
        const triggerCount = this.triggerTargets.length;

        if (triggerIndex === -1) return;

        // Prevents page scroll while user is navigating
        event.preventDefault();

        let nextIndex = triggerIndex;
        const homeIndex = 0;
        const endIndex = triggerCount - 1;

        const operations = {
            moveNext: () => {
                nextIndex = triggerIndex + 1;
                nextIndex = nextIndex > endIndex ? homeIndex : nextIndex;
            }, movePrev: () => {
                nextIndex = triggerIndex - 1;
                nextIndex = nextIndex < homeIndex ? endIndex : nextIndex;
            }
        };
        const keyMappings = {
            "Home": () => nextIndex = homeIndex, "End": () => nextIndex = endIndex, "ArrowRight": () => {
                if (orientation === 'horizontal') {
                    isDirectionLTR ? operations.moveNext() : operations.movePrev();
                }
            }, "ArrowDown": () => {
                if (orientation === 'vertical') {
                    operations.moveNext();
                }
            }, "ArrowLeft": () => {
                if (orientation === 'horizontal') {
                    isDirectionLTR ? operations.movePrev() : operations.moveNext();
                }
            }, "ArrowUp": () => {
                if ('vertical' === orientation) {
                    operations.movePrev();
                }
            }
        };

        const keyFunction = keyMappings[event.key];
        if (keyFunction) {
            keyFunction();
        }

        const clampedIndex = nextIndex % triggerCount;
        this.triggerTargets[clampedIndex]?.focus();
    }

    setElementState(element, isOpen) {
        const stateStr = isOpen ? 'open' : 'closed';
        element.dataset.state = stateStr;
    }

    setInitialContentProperties(contentElement, isOpen) {
        contentElement.hidden = isOpen;
        contentElement.style.setProperty('--radix-collapsible-content-height', contentElement.scrollHeight + "px");
        contentElement.style.setProperty('--radix-collapsible-content-width', contentElement.scrollWidth + "px");
        contentElement.hasBeenClicked = true;
    }

    closeLastOpenedElement(index) {
        if (this.lastOpenIndex !== undefined && this.lastOpenIndex !== index) {
            const lastContent = this.contentTargets[this.lastOpenIndex];
            const lastItem = this.itemTargets[this.lastOpenIndex];
            const lastTrigger = this.triggerTargets[this.lastOpenIndex];

            this.setElementState(lastContent, false);
            this.setElementState(lastItem, false);
            this.setElementState(lastTrigger, false);

            setTimeout(() => {
                lastContent.hidden = true;
            }, 180);
        }
    }

    toggle(event) {
        event.preventDefault();
        const trigger = event.currentTarget;

        const triggerIndex = parseInt(trigger.getAttribute("data-index"));
        const content = this.contentTargets[triggerIndex];
        const item = this.itemTargets[triggerIndex];

        if (item.hasAttribute('data-disabled')) return;

        if (this.typeValue !== 'multiple') {
            this.closeLastOpenedElement(triggerIndex);
        }

        const shouldOpen = content.dataset.state !== 'open';

        this.setElementState(content, shouldOpen);
        this.setElementState(item, shouldOpen);
        this.setElementState(trigger, shouldOpen);
        trigger.setAttribute('aria-expanded', shouldOpen);

        if (!content.hasBeenClicked) {
            this.setInitialContentProperties(content, !shouldOpen);
        } else {
            if (!shouldOpen) {
                setTimeout(() => {
                    content.hidden = true;
                }, 180);
            } else {
                content.hidden = false;
            }
        }

        this.lastOpenIndex = triggerIndex;
    }
}

