import {Controller} from "@hotwired/stimulus";
import '@kanety/stimulus-static-actions';

export default class UICollapsible extends Controller {
    static targets = ['trigger', 'content'];
    static actions = [
        ['trigger', 'click->toggle']
    ];

    setDataState(target, state) {
        target.setAttribute('data-state', state);
    }

    setAriaAttributes(target, expanded) {
        target.setAttribute('aria-expanded', expanded);
    }

    toggle() {
        const active = this.element.dataset.state === 'active';
        this.contentTarget.hidden = active;

        this.setDataState(this.element, active ? 'closed' : 'active');
        this.setDataState(this.triggerTarget, active ? 'closed' : 'active');
        this.setDataState(this.contentTarget, active ? 'closed' : 'active');
        this.setAriaAttributes(this.triggerTarget, active ? 'false' : 'true');
    }
}

