import {Controller} from "@hotwired/stimulus";

export default class UISelect extends Controller {
    static targets = ["item"];
    static outlets = ["ui--input", "ui--text"];

    connect() {
        this.setSelectedItem();
    }

    setSelectedItem() {
        const selectedItem = this.selectedItem();

        this.itemTargets.forEach((item) => {
            item.removeAttribute("data-selected");
            item.removeAttribute("aria-selected");
        });

        if (selectedItem) {
            selectedItem.setAttribute("data-selected", "true");
            selectedItem.setAttribute("aria-selected", "true");
        }
    }

    selectedItem() {
        return this.itemTargets.find((item) => {
            return item.dataset.value === this.uiInputOutlet.element.value;
        });
    }

    selectItem(event) {
        event.preventDefault();
        const selectedValue = event.currentTarget.dataset.value;
        const selectedText = event.currentTarget.innerText;

        this.setValue(selectedValue);
        this.setInnerText(selectedText);

        this.setSelectedItem()
    }

    setValue(selectedValue) {
        this.uiInputOutlet.setValue(selectedValue);
    }

    setInnerText(selectedOption) {
        this.uiTextOutlet.setText(selectedOption);
    }
}

